package practicelogics;

public class ghfd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int k=8;
	for(int i=1;i<=7;i++) {
		k--;
		for(int j=1;j<=7;j++) {
		if(i==j || j==k) {
			
			System.out.print("*");
		}	
		else {
			
			System.out.print(" ");
		}
			
			
			
		}
		
		System.out.println();
		
		
		
	}	
		
		
		
		
		
		

	}

}
