package practicelogics;

public class p1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
	int m;
	m=4;
	for(int i=1;i<=4;i++) {
		for(int j=1;j<=4;j++) {
			if(j==m) {
				System.out.print("*");
			}
			else {
				System.out.print(" ");
			}
			
			
		}
		m--;
		System.out.println();
		
		
		
		
	}
		
		
		
		
		
		

	}

}
