package practicelogics;

public class patter {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int k=9;
for(int i=4;i>=1;i--) {
	
	k--;
	
	for(int j=1;j<=8;j++) {
		
		if(j>=i && j<=k  ) {
			
			System.out.print("*");
			
		}
		
		else {
			
			System.out.print(" ");
		}
		
		
		
	}
	
	System.out.println();
	
	
	
	
	
	
}
		
		
		
		
		
		
		
		
		
	}

}
