package practicelogics;

public class pyra {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
	int k=5;
	for(int i=1;i<=5;i++) {
		for(int j=1;j<=5;j++) {
		if(k>=j) {
			
			System.out.print("*");
		}	
		
		else {
			System.out.print("      ");
		}
			
			
		}
		k--;
		System.out.println();
		
		
		
	}
		
		
		
		
		

		
	}

}
